import React from 'react'
import './product.css';
import { Link } from "react-router-dom";
import mug from '../../Images/mug.jpg';
import puzzle from '../../Images/puzzle.jpg';
import book from '../../Images/book.jpg';
const Product = () => {

    return (
        <>
            {
                products.map((product, ind) =>
                    <div key={ind} className='product' >
                        <img src={product.src} className='product_size' alt="" />
                        <div className='product_desc'>
                            <h2>{product.title}</h2>
                            <br />
                            <p>{product.desc}</p>
                            <br />
                            <h2>${product.price}</h2>
                        </div>
                        <div className='list_btn'>
                            <Link to={"/product/"} >
                                <button >View Item</button>
                            </Link>
                            <br />
                            <button className="add_btn">Add To Cart</button>
                        </div>
                    </div >
                )
            }
        </>

    )

}

export default Product
