import * as actionTypes from './shopping_types';
import mug from '../../components/Images/mug.jpg';
import book from '../../components/Images/book.jpg';
import puzzle from '../../components/Images/puzzle.jpg';


const INITIAL_STATE = {
    products: [
        {
            title: "Hydro Flask Mug - Stainless Steel",
            desc: `$93.82 Shipping & Import Fees Deposit to Pakistan ${<br />} Details Available at a lower price from other sellers that may not offer free Prime shipping.`,
            price: 27.95,
            img: mug,
            id: 1

        },
        {
            title: "Confidence Man",
            desc: "“Will be a primary source about the most vexing president in American history for years to come.” - Joe Klein, The New York Times",
            price: 22.95,
            img: book,
            id: 2
        },
        {
            title: "The Rubik's Cube",
            desc: "The Rubik's Cube is the world's best-known puzzle, a magical object that has baffled and fascinated the world for more than forty years. This clearly-illustrated step-by-step guide teaches you a foolproof beginners' method for solving the Cube, plus advanced techniques if you want to learn to solve it in seconds.",
            price: 14.10,
            img: puzzle,
            id: 3
        }

    ], // {id, title, desc, price, img}
    cart: [], // {id, title, desc, price, img, qty}
    currentItem: null
};

const shopReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case actionTypes.ADD_TO_CART:
            return {};
        case actionTypes.REMOVE_FROM_CART:
            return {};
        case actionTypes.ADJUST_QTY:
            return {};
        case actionTypes.LOAD_CURRENT_ITEM:
            return {};
        default:
            return state
    }
}

export default shopReducer;
