import React, { useState } from "react";
import "./CartItem.css";
import mug from '../../Images/mug.jpg'
import deleteBTN from '../../Images/deleteBTN.png'
import { useDispatch } from 'react-redux';
import { removeFromCart, adjustQTY } from '../../../redux/shopping/shopping_actions';


const CartItem = ({ product }) => {
  const [input, setInput] = useState(product.qty);
  const dispatch = useDispatch();

  // const onChangeHandler = (e) => {
  //   setInput(e.target.value);
  //   adjustQTY(product.id, e.target.value);
  // }
  return (
    <div className="cartItem">
      <img className="cartItem__image" src={product.img} alt="" />
      <div className="cartItem__details">
        <p className="details__title">{product.title}</p>
        <p className="details__desc">{product.desc}</p>
        <p className="details__price">${product.price}</p>
      </div>
      <div className="cartItem__actions">
        <div className="cartItem__qty">
          <label htmlFor="qty" style={{ fontWeight: "bold" }}>Qty</label>
          <input
            style={{ border: "1.5px solid gray" }}
            min="1"
            type="number"
            id="qty"
            name="qty"
            value={input}
            onChange={(e) => {dispatch(adjustQTY(product.id, e.target.value)) }}
          />
        </div>
        <button className="actions__deleteItemBtn" onClick={() => dispatch(removeFromCart(product.id))}>
          <img src={deleteBTN} alt="delte Button" />
        </button>
      </div>
    </div>
  );
};

export default CartItem;
