import React, { useState, useEffect } from "react";
import "./Cart.css";
import CartItem from "./CartItem/CartItem";
import { useSelector } from "react-redux";

const Cart = () => {
  const cartArray = useSelector(state => state.shop.cart);
  console.log(cartArray);
  const [total, setTotal] = useState({
    qty: "",
    price: ""
  });
console.log()

  useEffect(() => {
    cartArray.forEach((obj) => {
      let qtyInput = 0;
      let priceInput = 0;
      qtyInput += obj.qty;
      priceInput += obj.price * obj.qty;
      setTotal({ ...total, [total.qty]: qtyInput });
      // setTotal({ ...total, [total.price]: priceInput });
    })
  }, [cartArray, total, setTotal])

  return (
    <div className="cart">
      <div className="cart__items">
        {
          cartArray.map(obj => <CartItem key={obj.id} productData={obj} />)
        }
      </div>
      <div className="cart__summary">
        <h4 className="summary__title">Cart Summary</h4>
        <div className="summary__price">
          <span style={{ fontWeight: "bold" }}>TOTAL: {total.qty}{" "}Items</span>
          <span>${total.price}</span>
        </div>
        <button className="summary__checkoutBtn">
          Proceed To Checkout
        </button>
      </div>
    </div>
  );
};


export default Cart;
