import React from 'react'
import './product.css';
import { Link } from "react-router-dom";
import mug from '../../Images/mug.jpg';
import puzzle from '../../Images/puzzle.jpg';
import book from '../../Images/book.jpg';
const Product = () => {

    const products = [
        {
            title: "Hydro Flask Mug - Stainless Steel",
            desc: "$93.82 Shipping & Import Fees Deposit to Pakistan Details Available at a lower price from other sellers that may not offer free Prime shipping.",
            price: 27.95,
            src: mug,
            id: 1

        },
        {
            title: "Confidence Man",
            desc: "“Will be a primary source about the most vexing president in American history for years to come.” - Joe Klein, The New York Times",
            price: 22.95,
            src: book,
            id: 2
        },
        {
            title: "The Rubik's Cube",
            desc: "The Rubik's Cube is the world's best-known puzzle, a magical object that has baffled and fascinated the world for more than forty years. This clearly-illustrated step-by-step guide teaches you a foolproof beginners' method for solving the Cube, plus advanced techniques if you want to learn to solve it in seconds.",
            price: 14.10,
            src: puzzle,
            id: 3
        }
    ]

    return (
        <>
            {
                products.map((product, ind) =>
                    <div div className='product' >
                        <img src={product.src} className='product_size' alt="" />
                        <div className='product_desc'>
                            <h2>{product.title}</h2>
                            <br />
                            <p>{product.desc}</p>
                            <br />
                            <h2>${product.price}</h2>
                        </div>
                        <div className='list_btn'>
                            <Link to={"/product/"} >
                                <button >View Item</button>
                            </Link>
                            <br />
                            <button className="add_btn">Add To Cart</button>
                        </div>
                    </div >
                )
            }
        </>

    )

}

export default Product
