        <div className='add_to_cart'>
          {/* <h3 className='cart'>Cart</h3> */}
          <img src={cartIcon} className='baskit' alt="" />
          <h3 className='cart_counte'>{cartCount}</h3>
        </div>
      </Link>
    </div >
  )
}

export default Navbar
