import { addToCart } from "../../redux/shopping/shopping_actions";

const SingleItem = () => {
  const productsArray = useSelector(state => state.shop.products);
  const dispatch = useDispatch();

  return (
    <>
      {
        < div className="singleItem" >
          <img className="singleItem__image" src={mug} alt={""} />
          <div className="singleItem__details">
            <p className="details__title">Hydro Flask Mug - Stainless Steel</p>
            <p className="details__description">$93.82 Shipping & Import Fees  to Pakistan <br /> Details Available at a lower price from other sellers that may not offer <br /> free Prime shipping.</p>
            <p className="details__price">$27.95</p>
            <button className="details__addBtn" onClick={() => { dispatch(addToCart(productID.id)) }}>Add To Cart</button>
          </div>
        </ div>
      }
    </>
  );
};

export default SingleItem;
