import React from 'react';
import './products.css';
import Product from './product/Product';
import { useSelector } from 'react-redux';

const Products = () => {
    const products = useSelector((state) => { return state.shop });
    const text = products.map((product) => { return product.text });
    console.log(text);

    return (
        <div className='product_list'>
            <Product />
        </div>
    )
}

export default Products;
